const uploadResponse = items[0].json;
const telegramData = $('Disparador de Telegram').item.json;
const fileId = telegramData.message.photo[1].file_id;
const timestamp = new Date().getTime();
const date = new Date().toISOString().split('T')[0];

// Obtener el nombre del archivo desde la respuesta de Supabase
const actualFileName = uploadResponse.Key || uploadResponse.Id || uploadResponse.path || `${date}/${timestamp}-${fileId}.jpg`;

const publicUrl = `https://rrmafdbxvimmvcerwguy.supabase.co/storage/v1/object/public/imagenes/${actualFileName}`;

return {
  json: {
    success: true,
    id: actualFileName,
    name: actualFileName.split('/').pop(),
    fileName: actualFileName,
    fullPath: actualFileName,
    webViewLink: publicUrl,
    publicUrl: publicUrl,
    Key: actualFileName,
    bucket: 'imagenes',
    uploadedAt: new Date().toISOString(),
    originalMessage: telegramData.message.caption || '',
    chatId: telegramData.message.chat.id
  }
};

