const respuestaSubida = items[0].json;
const datosTelegram = $('Disparador de Telegram').item.json;
const idArchivo = datosTelegram.message.photo[1].file_id;
const marcaTiempo = new Date().getTime();
const fecha = new Date().toISOString().split('T')[0];

// Obtener el nombre del archivo desde la respuesta de Supabase
let nombreArchivoReal = respuestaSubida.Key || respuestaSubida.Id || respuestaSubida.path || `${fecha}/${marcaTiempo}-${idArchivo}.jpg`;

// CORREGIR: Eliminar 'imagenes/' del inicio si ya est√° presente para evitar duplicaci√≥n
if (nombreArchivoReal.startsWith('imagenes/')) {
  nombreArchivoReal = nombreArchivoReal.replace('imagenes/', '');
}

// Generar URL p√∫blica correcta sin duplicar 'imagenes'
const urlPublica = `https://rrmafdbxvimmvcerwguy.supabase.co/storage/v1/object/public/imagenes/${nombreArchivoReal}`;

return {
  json: {
    exito: true,
    id: nombreArchivoReal,
    nombre: nombreArchivoReal.split('/').pop(),
    nombreArchivo: nombreArchivoReal,
    rutaCompleta: nombreArchivoReal,
    enlaceVisualizacion: urlPublica,
    urlPublica: urlPublica,
    Clave: nombreArchivoReal,
    bucket: 'imagenes',
    fechaSubida: new Date().toISOString(),
    mensajeOriginal: datosTelegram.message.caption || '',
    idChat: datosTelegram.message.chat.id
  }
};

