if (respuestaAgent.json && respuestaAgent.json.output) {
  respuestaTexto = String(respuestaAgent.json.output).trim();
} else if (respuestaAgent.json && respuestaAgent.json.text) {
  respuestaTexto = String(respuestaAgent.json.text).trim();
} else if (respuestaAgent.json) {
  respuestaTexto = JSON.stringify(respuestaAgent.json);
} else {
  respuestaTexto = String(respuestaAgent).trim();
}

console.log('üìù Respuesta completa:', respuestaTexto.substring(0, 200) + '...');
console.log('üìè Longitud:', respuestaTexto.length);

// DETECCI√ìN ROBUSTA DE JSON DE PRODUCTO
let esProductoJSON = false;
let objetoProducto = null;
let razonDeteccion = '';

// Solo procesar si tiene contenido sustancial
if (respuestaTexto.length > 50) {

  // NUEVA L√ìGICA: Buscar JSON dentro del texto (incluso si hay texto conversacional)
  let jsonExtraido = null;

  // M√©todo 1: Buscar JSON entre ```json y ```
  const matchCodeBlock = respuestaTexto.match(/```json\s*([\s\S]*?)\s*```/);
  if (matchCodeBlock && matchCodeBlock[1]) {
    console.log('üîç JSON encontrado en bloque de c√≥digo');
    try {
      jsonExtraido = JSON.parse(matchCodeBlock[1].trim());
    } catch (error) {
      console.log('‚ùå Error parseando JSON del bloque:', error.message);
    }
  }

  // M√©todo 2: Buscar JSON que empiece con { y termine con } (m√°s robusto)
  if (!jsonExtraido) {
    const inicioJSON = respuestaTexto.indexOf('{');
    const finJSON = respuestaTexto.lastIndexOf('}');

    if (inicioJSON !== -1 && finJSON !== -1 && finJSON > inicioJSON) {
      const posibleJSON = respuestaTexto.substring(inicioJSON, finJSON + 1);
      console.log('üîç Posible JSON encontrado en posici√≥n', inicioJSON, 'a', finJSON);

      try {
        jsonExtraido = JSON.parse(posibleJSON);
        console.log('‚úÖ JSON parseado exitosamente');
      } catch (error) {
        console.log('‚ùå Error parseando JSON extra√≠do:', error.message);
      }
    }
  }

  // M√©todo 3: Verificar si toda la respuesta es JSON
  if (!jsonExtraido && respuestaTexto.startsWith('{') && respuestaTexto.endsWith('}')) {
    try {
      jsonExtraido = JSON.parse(respuestaTexto);
      console.log('‚úÖ Respuesta completa es JSON v√°lido');
    } catch (error) {
      console.log('‚ùå Error parseando respuesta completa como JSON:', error.message);
    }
  }

  // VALIDAR SI EL JSON EXTRA√çDO ES UN PRODUCTO
  if (jsonExtraido) {
    console.log('üîç Validando JSON extra√≠do...');
    console.log('   - Tiene nombre:', !!jsonExtraido.nombre);
    console.log('   - Tiene precio:', jsonExtraido.precio !== undefined);
    console.log('   - Tiene descripcion:', !!jsonExtraido.descripcion);

    // Verificar campos m√≠nimos de producto
    if (jsonExtraido.nombre && 
        (jsonExtraido.precio !== undefined) && 
        jsonExtraido.descripcion) {

      esProductoJSON = true;
      objetoProducto = jsonExtraido;
      razonDeteccion = 'JSON de producto v√°lido extra√≠do de respuesta';

      console.log('‚úÖ PRODUCTO DETECTADO:', jsonExtraido.nombre);
      console.log('üí∞ Precio:', jsonExtraido.precio);
      console.log('üìã Campos:', Object.keys(jsonExtraido).length);
    } else {
      razonDeteccion = 'JSON encontrado pero faltan campos de producto';
      console.log('‚ùå JSON encontrado pero no es producto v√°lido');
    }
  } else {
    razonDeteccion = 'No se encontr√≥ JSON v√°lido en la respuesta';
    console.log('‚ùå No se pudo extraer JSON v√°lido');
  }
} else {
  razonDeteccion = 'Respuesta muy corta - conversacional';
  console.log('üìè Respuesta muy corta');
}

console.log('üéØ RESULTADO FINAL:');
console.log('   - Es producto:', esProductoJSON);
console.log('   - Raz√≥n:', razonDeteccion);

// IMPORTANTE: El IF espera producto_detectado como STRING "true"
return {
  json: {
    // Datos originales
    output: respuestaAgent.json?.output || respuestaTexto,

    // CAMPO CLAVE PARA EL IF (como string "true" o "false")
    producto_detectado: esProductoJSON ? "true" : "false",

    // Campos adicionales para informaci√≥n
