console.log('‚úÖ PROCESANDO PRODUCTO - producto_detectado es true');

// EXTRAER EL PRODUCTO JSON DEL DETECTOR
let productoData = null;

// El detector ahora pone el producto parseado en 'producto_objeto'
if (respuestaAgent.json && respuestaAgent.json.producto_objeto) {
