// Procesar JSON del producto completo
const output = $input.first().json.output;

// Extraer el JSON despuÃ©s de ***PRODUCTO_LISTO***
const jsonStart = output.indexOf('***PRODUCTO_LISTO***') + '***PRODUCTO_LISTO***'.length;
const jsonString = output.substring(jsonStart).trim();

try {
  const producto = JSON.parse(jsonString);

  return {
    json: {
      producto_generado: producto,
      json_limpio: jsonString,
      id_sesion: $input.first().json.id_sesion,
      tipo_respuesta: 'producto_completo'
    }
  };
} catch (error) {
  return {
    json: {
      error: 'Error al procesar JSON del producto',
      output_original: output,
      id_sesion: $input.first().json.id_sesion,
      tipo_respuesta: 'error'
    }
  };
}

